import React, { useEffect, useState } from 'react';

const API = 'http://localhost:5000';

function App() {
  const [allRecipes, setAllRecipes] = useState([]);
  const [ingredientsText, setIngredientsText] = useState('');
  const [results, setResults] = useState([]);
  const [diet, setDiet] = useState('');
  const [maxTime, setMaxTime] = useState('');
  const [favorites, setFavorites] = useState(() => {
    try { return JSON.parse(localStorage.getItem('fav')) || []; } catch { return []; }
  });

  useEffect(() => {
    fetch(API + '/api/recipes')
      .then(r => r.json())
      .then(setAllRecipes);
  }, []);

  function search() {
    const q = [];
    if (ingredientsText) q.push('ingredients=' + encodeURIComponent(ingredientsText));
    if (diet) q.push('diet=' + encodeURIComponent(diet));
    if (maxTime) q.push('maxTime=' + encodeURIComponent(maxTime));
    const url = API + '/api/search' + (q.length ? ('?' + q.join('&')) : '');
    fetch(url).then(r => r.json()).then(setResults);
  }

  return (
    <div style={{padding:20,fontFamily:'Arial'}}>
      <h1>Smart Recipe Generator</h1>

      <div style={{display:'flex',gap:20}}>
        <div style={{flex:1}}>
          <h3>Inputs</h3>
          <textarea
            value={ingredientsText}
            onChange={e=>setIngredientsText(e.target.value)}
            placeholder="type ingredients separated by commas"
            rows={3}
            style={{width:'100%'}} />

          <div style={{marginTop:8}}>
            <label>Diet: </label>
            <select value={diet} onChange={e=>setDiet(e.target.value)}>
              <option value="">Any</option>
              <option value="vegetarian">Vegetarian</option>
              <option value="vegan">Vegan</option>
              <option value="non-vegetarian">Non-vegetarian</option>
            </select>

            <label style={{marginLeft:12}}>Max time (mins): </label>
            <input
              type="number"
              value={maxTime}
              onChange={e=>setMaxTime(e.target.value)}
              style={{width:80}} />
          </div>

          <div style={{marginTop:8}}>
            <button onClick={search}>Find Recipes</button>
          </div>

          <h3 style={{marginTop:20}}>Favorites</h3>
          <ul>
            {favorites.map(id => {
              const r = allRecipes.find(x=>x.id===id);
              return r ? <li key={id}>{r.name}</li> : null;
            })}
          </ul>
        </div>

        <div style={{flex:2}}>
          <h3>Results</h3>
          {results.length===0 && <p>No results yet. Try entering ingredients and click "Find Recipes".</p>}
          {results.map(r => (
            <div key={r.id} style={{border:'1px solid #ddd',padding:10,marginBottom:8}}>
              <h4>{r.name} ({r.time} min)</h4>
              <div>Diet: {r.diet}</div>
              <div><strong>Ingredients:</strong> {r.ingredients.join(', ')}</div>
              <div>
                <strong>Steps:</strong>
                <ol>{r.steps.map((s,i)=><li key={i}>{s}</li>)}</ol>
              </div>
              <div><strong>Nutrition:</strong> {Object.entries(r.nutrition).map(([k,v])=>`${k}: ${v}`).join(', ')}</div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
